mapper.select:
SELECT id, username, nickname, avatar, email, bio, status, role, create_time, update_time FROM user_profile
AND (username LIKE CONCAT('%', #{keyword}, '%') OR nickname LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%'))
AND status = #{status}
AND role = #{role}
ORDER BY create_time DESC

SELECT id, username, nickname, avatar, email, bio, status, role, create_time, update_time
FROM user_profile
WHERE id = #{id}

mapper.update:
UPDATE user_profile
SET status = #{status}
WHERE id = #{id}

UPDATE user_profile
SET role = #{role}
WHERE id = #{id}
