mapper.select:
SELECT p.*, u.username, u.nickname, u.avatar FROM post p LEFT JOIN user_profile u ON p.user_id = u.id
AND p.status = #{status}
ORDER BY p.create_time DESC

SELECT p.*, u.username, u.nickname, u.avatar FROM post p LEFT JOIN user_profile u ON p.user_id = u.id WHERE p.user_id = #{userId}
AND p.status = #{status}
ORDER BY p.create_time DESC

SELECT 
    p.*,
    u.username, u.nickname, u.avatar
FROM post p
LEFT JOIN user_profile u ON p.user_id = u.id
WHERE p.status = 1
AND (
    p.user_id = #{userId}
    OR p.user_id IN (
        SELECT following_id FROM follow_relation WHERE follower_id = #{userId}
    )
)
ORDER BY p.create_time DESC

mapper.update:
UPDATE post
content = #{content}, images = #{images}, type = #{type}, status = #{status},
WHERE id = #{id}

UPDATE post
SET status = 0
WHERE id = #{id}

UPDATE post
SET like_count = like_count + 1
WHERE id = #{id}

UPDATE post
SET comment_count = comment_count + 1
WHERE id = #{id}

UPDATE post
SET view_count = view_count + 1
WHERE id = #{id}

mapper.select:
SELECT p.*, u.username, u.nickname, u.avatar,
(SELECT COUNT(*) FROM post_like WHERE post_id = p.id AND user_id = #{currentUserId}) > 0 AS is_liked
FROM post p LEFT JOIN user_profile u ON p.user_id = u.id WHERE p.id = #{id}
