mapper.update:
UPDATE user_profile
nickname = #{nickname}, avatar = #{avatar}, email = #{email}, bio = #{bio}, status = #{status},
WHERE id = #{id}

UPDATE user_profile
SET password = #{password}
WHERE id = #{id}

UPDATE user_profile
SET avatar = #{avatar}
WHERE id = #{id}

mapper.select:
SELECT u.*, (SELECT COUNT(*) FROM follow_relation WHERE follower_id = u.id) AS following_count, (SELECT COUNT(*) FROM follow_relation WHERE following_id = u.id) AS follower_count, (SELECT COUNT(*) FROM post WHERE user_id = u.id AND status = 1) AS post_count,
(SELECT COUNT(*) FROM follow_relation WHERE follower_id = #{currentUserId} AND following_id = u.id) > 0 AS is_followed
FROM user_profile u WHERE u.id = #{id}
